import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Post } from '../Post/Post';
import { fetchPostsRequest } from '../../reducers/postsReducer'; // Import action creator for fetching posts
import './Home.css';

export const Home = (props) => {
  const dispatch = useDispatch();
  const posts = useSelector(state => state.posts_red.posts);

  useEffect(() => {
    dispatch(fetchPostsRequest(props.p)); // Fetch posts based on the selected subreddit
  }, [dispatch, props.p]);

  return (
    <div className="home">
      <h1>Reddit Posts</h1>
      <ul>
        {posts.map(post => (
          <li key={post.id}>
            <Post post={post}/>
          </li>
        ))}
      </ul>
    </div>
  );
};
