export const changeSubreddit = (subreddit) => ({
  type: CHANGE_SUBREDDIT,
  payload: subreddit
});


// rootReducer.js



export const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case CHANGE_SUBREDDIT:
      return { ...state, subreddit: action.payload };
    default:
      return state;
  }
};
