import React from 'react';
import { useDispatch } from 'react-redux';
import { fetchPostsRequest } from '../../reducers/postsReducer';
import { isCompositeComponent } from 'react-dom/test-utils';
import { changeSubreddit } from '../../reducers/subredditReducer';

export const Sidebar = () => {
  const dispatch = useDispatch();
  const handleClick = (subreddit) => {
    dispatch(changeSubreddit(subreddit));
  };

  return (
    <div className="sidebar">
      <h2>Subreddits</h2>
      <button onClick={() => handleClick('/r/Arsenal')}>Arsenal</button>
      <button onClick={() => handleClick('/r/ManCity')}>Man City</button>
    </div>
  );
};
