import React from 'react';
import { useDispatch } from 'react-redux';
import { changeSubreddit } from '../../reducers/subredditReducer'; // Import the action creator


const YourComponent = () => {
  const dispatch = useDispatch();

  const handleSubredditChange = (newSubreddit) => {
    dispatch(changeSubreddit(newSubreddit)); // Dispatch the action with the new subreddit
  };

  return (
    <div>
      {/* Example of changing the subreddit */}
      <button onClick={() => handleSubredditChange('/r/Arsenal')}>
        Change Subreddit to Arsenal
      </button>
      <button onClick={() => handleSubredditChange('/r/ManCity')}>
        Change Subreddit to Man City
      </button>
    </div>
  );
};
