import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Post from '../Post/Post';
import PostLoading from '../Post/PostLoading';
import { fetchPosts, selectFilteredPosts, setSearchTerm, fetchComments } from '../../store/redditSlice';
import './Home.css';

const Home = () => {
  const reddit = useSelector((state) => state.reddit);
  const { isLoading, error, searchTerm, selectedSubreddit } = reddit;
  const posts = useSelector(selectFilteredPosts);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchPosts(selectedSubreddit));
  }, [selectedSubreddit, dispatch]);

  const onToggleComments = (index) => {
    const getComments = (permalink) => {
      dispatch(fetchComments(index, permalink));
    };
    return getComments;
  };

  return (
    <>
      {isLoading ? (
        <div className="loading">
          {Array.from({ length: 5 }, (_, i) => (
            <PostLoading key={i} />
          ))}
        </div>
      ) : error ? (
        <div className="error">
          <h2>Failed to load posts.</h2>
          <button type="button" onClick={() => dispatch(fetchPosts(selectedSubreddit))}>
            Try again
          </button>
        </div>
      ) : posts.length === 0 ? (
        <div className="error">
          <h2>No posts matching "{searchTerm}"</h2>
          <button type="button" onClick={() => dispatch(setSearchTerm(''))}>
            Go home
          </button>
        </div>
      ) : (
        <div className="posts">
          {posts.map((post, index) => (
            <Post key={post.id} post={post} onToggleComments={onToggleComments(index)} />
          ))}
        </div>
      )}
    </>
  );
};

export default Home;
