// index.js
import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import { Provider } from 'react-redux';
import { Card } from './components/Card/Card';
import { Home } from './features/Home/Home';
import { createStore, combineReducers, useState } from 'redux';
import { postsReducer } from './reducers/postsReducer'; // Import your postsReducer
import { counterReducer } from './reducers/counterReducer';
import { commentsReducer } from './reducers/commentsReducer';
import { Sidebar } from './features/Sidebar/Sidebar';


// Redux store
export const rootReducer = combineReducers({
  posts_red: postsReducer, // This key 'posts' will be used to access the posts slice of state
  // Add other reducers here if needed
  counter_red: counterReducer,
  comments_red: commentsReducer
});

export const store = createStore(rootReducer);

// Action creators
export const increment = () => ({
  type: "increment",
  payload: 1
});
export const divide = () => ({
  type: "divide",
  payload: 2
});

export const decrement = () => ({
  type: "decrement",
  payload: 1
});

export const multiply = () => ({
  type: "multiply",
  payload: 2
});

export const squared = () => ({
  type: "squared",
  payload: 2
});
export const reset = () => ({
  type: "reset",
});

// Render the app
ReactDOM.render(
  <Provider store={store}>
    <React.StrictMode>
      <App />
      <Home p = '/r/FantasyPL'/>
      <Sidebar></Sidebar>
    </React.StrictMode>
  </Provider>,
  document.getElementById('root')
);

// Log initial state and subscribe to store changes

const ParentComponent = () => {
  const [currentSubreddit, setCurrentSubreddit] = useState('/r/FantasyPL');

  const handleChangeSubreddit = (subreddit) => {
    setCurrentSubreddit(subreddit);
  };

  return (
    <div>
      <Sidebar changeSubreddit={handleChangeSubreddit} />
      <Home p={currentSubreddit} />
    </div>
  );
};
