import React, { useState } from 'react';
import './Sidebar.css';

export const Sidebar = ({ onSelectSubreddit }) => {
  const [selectedSubreddit, setSelectedSubreddit] = useState(""); // State to track selected subreddit

  const handleSubredditClick = (subreddit) => {
    setSelectedSubreddit(subreddit); // Update the selected subreddit state
    onSelectSubreddit(subreddit); // Pass the selected subreddit to the parent component
  };

  return (
    <div className="sidebar">
      <h2>Subreddits</h2>
      <button onClick={() => handleSubredditClick("/r/Arsenal")}>Arsenal</button>
      <button onClick={() => handleSubredditClick("/r/ManCity")}>Man City</button>
    </div>
  );
};
