import logo from './logo.svg';
import './App.css';
import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { increment, decrement} from './index';



function App() {
  const dispatch = useDispatch();
  const counter = useSelector(state => state.counter); // Accessing the counter state from Redux store

  const handleIncrement = () => {
    dispatch(increment()); // Dispatch increment action
  };

  const handleDecrement = () => {
    dispatch(decrement()); // Dispatch decrement action
  };



  return (
    <div className="App">
      <h1>Hello Redux</h1>
      <button onClick={increment}>Increment by 1</button>
      <button onClick={decrement}>Decrement by 1</button>
      <p> state: {counter}</p>
    </div>
  );
};


export default App;
