// Define the initial state
const initialState = {
  searchTerm: '', // Initial search term is empty
};
const SET_SEARCH_TERM = 'SET_SEARCH_TERM';






export const setSearchTerm = (searchTerm) => ({
    type: SET_SEARCH_TERM,
    payload: searchTerm,
  });
