import React, { useState } from 'react';
import './Post.css';
import Card from '../../components/Card/Card';

const Post = ({ props }) => {
  const { title, author, thumbnail, ups, num_comments } = props;

  return (
    <Card className="post">
      <div className="post-wrapper">
        <div className="post-container">
          <h2 className="post-title">{title}</h2>
          <div className="author-details">
            <p className="author-username">Posted by {author}</p>
          </div>
          <div className="post-details">
            <div className="post-votes-container">
              <button className="icon-action-button up-vote" title="Upvote">
                &#9650;
              </button>
              <span className="post-votes-value">{ups}</span>
              <button className="icon-action-button down-vote" title="Downvote">
                &#9660;
              </button>
            </div>
            <div className="post-comments-container">
              <button className="icon-action-button" title="Comments">
                <span className="icon-action">&#128172;</span>
                <span>{num_comments}</span>
              </button>
            </div>
          </div>
        </div>
        <div className="post-image-container">
          <img src={thumbnail} alt="Post Thumbnail" className="post-image" />
        </div>
      </div>
    </Card>
  );
};

export default Post;
