import React, { useState } from 'react';
import './Post.css';
import Card from '../../components/Card/Card';
import { API_ROOT } from '../../api/reddit';



export const Post = ({ post, a }) => {



  const [comments, setComments] = useState([]);

 const fetchComments = async (subreddit) => {
    try {
      // Make API call to fetch comments for the post
      // Replace 'YOUR_API_ENDPOINT' with the actual Reddit API endpoint for comments
      const response = await fetch(`${API_ROOT}${subreddit/comments}.json`);
      const data = await response.json();
      setComments(data.comments); // Assuming the response contains an array of comments
    } catch (error) {
      console.error('Error fetching comments:', error);
    }
  };


  return (
    <Card className="card">
      <div>
        <h2>{post.title}</h2>
        <p>Author: {post.author}</p>
        <p>{post.data}</p>
        <p>Number of Comments: {post.num_comments}</p>
        <button onClick={fetchComments(a)}>Comments</button>
      </div>
    </Card>
  );
};
