import logo from './logo.svg';
import './App.css';
import { increment, decrement, divide, multiply, squared, reset} from './index';
import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Home } from './features/Home/Home'; // Import Home component
import { Sidebar } from './features/Sidebar/Sidebar'; // Import Sidebar component

function App() {
  const dispatch = useDispatch();
  const count = useSelector(state => state.counter_red.counter);
  const [subreddit, setSubreddit] = useState('/r/FantasyPL');

  const handleIncrement = () => {
    dispatch(increment());
  };

  const handleDecrement = () => {
    dispatch(decrement());
  };

  const handleDivide = () => {
    dispatch(divide());
  };

  const handleMultiply = () => {
    dispatch(multiply());
  };

  const handleSquared = () => {
    dispatch(squared());
  };

  const Reset = () => {
    dispatch(reset());
  };

  const handleChangeSubreddit = (subreddit) => {
    setSubreddit(subreddit);
  };

  return (
    <div className="App">
      <h1>Hello Redux</h1>
      <button onClick={handleIncrement}>Increment by 1</button>
      <button onClick={handleDecrement}>Decrement by 1</button>
      <button onClick={handleDivide}>Divide by 2</button>
      <br></br>
      <button onClick={handleMultiply}>Multiply by 2</button>
      <button onClick={handleSquared}>Square</button>
      <br></br>
      <button onClick={Reset}>Reset</button>
      <p> state: {count}</p>

      {/* Render the Sidebar component and pass the handleChangeSubreddit function */}
      <Sidebar setSubreddit={handleChangeSubreddit} />

      {/* Render the Home component and pass the current subreddit */}
      <Home p={subreddit} />
    </div>
  );
}

export default App;
