import { useDispatch, useSelector } from 'react-redux';
import { Post } from '../Post/Post';
import { getSubredditPosts } from '../../api/reddit'; // Import the API function
import './Home.css';


export const Home = () => {
  const dispatch = useDispatch();
  const posts = useSelector(state => state.posts_red.posts); // Assuming you have a posts slice in your Redux store


  if (Array.isArray(posts) && posts.length > 0) {
    return (
      <div className="home">
        <h1>Reddit Posts</h1>
        {posts.map(post => (
          <Post key={post.id} post={post} />
        ))}
      </div>
    );
  } else if (Array.isArray(posts) && posts.length === 0) {
    return (
      <div className="home">
        <h1>Reddit Posts</h1>
        <p>No posts to display</p>
      </div>
    );
