import logo from './logo.svg';
import './App.css';
import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { increment, decrement} from './index';



function App() {
  const dispatch = useDispatch();
  const counter = useSelector(state => state.counter); // Accessing the counter state from Redux store

  const handleIncrement = () => {
    dispatch(increment()); // Dispatch increment action
  };

  const handleDecrement = () => {
    dispatch(decrement()); // Dispatch decrement action
  };
  const handleDivide = () => {
    dispatch(decrement()); // Dispatch decrement action
  };



  return (
    <div className="App">
      <h1>Hello Redux</h1>
      <button onClick={handleIncrement}>Increment by 1</button>
      <button onClick={handleDecrement}>Decrement by 1</button>
      <button onClick={handleDivide}>Divide by 2</button>
      <p> state: {counter}</p>
    </div>
  );
};


export default App;
